import React,{Component} from 'react';
import './App.css';
import Signin from './Signin/signin' ;
import Register from './Register/Register' ;
import Clarifai from 'clarifai' ;
import Particles from 'react-particles-js';
import Navigation from './Navigation/navigation';
import Logo from './LOGO/Logo';
import Imag from './Image/Image';
import Rank from './rank/Rank' ;
import Facedetect from './Face/facedetection.js';
const app = new Clarifai.App({
  apiKey: 'f656cc73e40844d5a5d984cc88c3d102'
 });
const particlesop = {
  particles: {
    number: {
      value : 75,
      density :{
        enable: true,
        value_area: 800
      }

    } 
  }
}
class App extends Component{
  constructor(){
    super() ;
    this.state = {
      input: '' ,
      imageurl: '' ,
      box: {} ,
      route: 'Signin'
    }
  }
  Calculatefacebox = (data) =>{
    const box = data.outputs[0].data.regions[0].region_info.bounding_box ;
    const image = document.getElementById('inputimage') ;
    const width = Number(image.width) ;
    const height = Number(image.height) ;
    console.log(width,height) ;
    return {
      leftCol: box.left_col * width ,
      topRow: box.top_row * height ,
      rightCol: width - (box.right_col * width) ,
      bottomRow: height - (box.bottom_row * height) 
    }
  }
  displayFaceBox = (box) => {
    this.setState({box: box}) ;
    console.log(box) ;
  }
  onInputchange =(event)=>{
    this.setState({input : event.target.value}) ;
  }
  onButtonSubmit = ()=>{
    this.setState({imageurl : this.state.input}) ;
    app.models.predict(
      Clarifai.FACE_DETECT_MODEL , 
      this.state.input)
    .then(response =>{ console.log(response) ; return(this.displayFaceBox(this.Calculatefacebox(response))) ;}) 
     .catch(err => console.log(err)) ;
  }
  onRouteChange = (route)=>{
    this.setState({route:route}) ;
  }
  render(){
      return (
    <div className="App">
      <Particles className="part" 
                params={particlesop} />
    {this.state.route === 'Signin'
     ? <Signin onRouteChange={this.onRouteChange} />
    : (
      this.state.route === 'Register' ? <Register />
    : <div>
     <Navigation onRouteChange={this.onRouteChange} />
     <Logo />
     <Rank />
     <Imag onInputchange={this.onInputchange} onButtonSubmit={this.onButtonSubmit} />
    <Facedetect imageUrl={this.state.imageurl} box={this.state.box} /> 
    </div>)
    }
    </div>
  ); }
}

export default App ;
// NOTE :- If we want to destructure and intead of say this.state.imageurl we want to use just
//imageurl ignoring the this. part , so we basically have to do at the beginning of the render ,
// {input , imageurl , box , route} = this.state ;
